public class WHileLoope_08 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i = 1;
		while (i <= 10) {

			System.out.println("Hiii u are doing good");
			i++;

		}

		while (i >= 10) {

			System.out.println("Hiii u are doing good");
			i++;

		}

	}

}
